"use strict";
var CustomElements = {};
document.addEventListener("DOMContentLoaded", function() {
	new MutationObserver(function(e) {
		e.forEach(function(e) {
			console.log("mutation on", e.target), t(e.target)
		})
	}).observe(document.body, {
		childList: !0,
		subtree: !0
	});
	var t = function(t) {
		for (var e = [], n = document.querySelectorAll("template"), o = 0; o < n.length; o++)
			for (var a = t.querySelectorAll(n[o].id), l = 0; l < a.length; l++) e.push({
				tag: a[l],
				template: n[o]
			});
		e.forEach(function(t) {
			console.log("add CustomElement", t.tag, t.template), t.tag.innerHTML = t.template.innerHTML;
			var e = new CustomElements[t.template.id](t.tag);
			e.DOM = t.tag, t.tag.CE = e, "connectedCallback" in e && e.connectedCallback()
		})
	};
	t(document.body)
});